import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
    container:{
      flex:1,
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: "#003d40"
    },

    text:{

      //cor do titulo , tamanho e tipo da fonte
      color: "#fff",
      fontSize:25,
      fontWeight:'bold',
      textAlign: 'center',
  },

  })

  export default styles
