import { StyleSheet } from 'react-native'
 
const styles = StyleSheet.create( {

  boxTitle: {
   
      //alinhando ao centro 
      alignItems: 'center',
      justifyContent:'center',
      padding: 10,
     
  },

  textTitle:{

      //cor do titulo , tamanho e tipo da fonte
      color:"#fff",
      fontSize:44,
      fontWeight:'bold',
      textAlign: 'center',
      
  },


  });

  export default styles
